    elif payload['type'] = 'Create' and payload['type'] = 'Update':
        return {'error': 'Unrecognized type field in payload'}, 400
    return {}, 200

def handle_create_request(payload):
    if 'name' in payload and payload['name'] == 'UnsafeDeserialization':
